package firstpkg;

public class Elseif {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		// ELSE IF
				int a1=20,b1=80,c1=60;
				if (a1>b1 && a1>c1)
				{
					System.out.println("a is greater");
				}
				else if (b1>a1 && b1>c1 )
				{
					System.out.println("b is greater");
				}
				else if (c1>a1 && c1>b1)
				{
					System.out.println("c is greater");
				}

	}

}
